import { useParams } from "react-router-dom";
import { useCart } from "./context/CartContext";
import { useWishlist } from "./context/WishlistContext";
import { PRODUCTS as CATALOG } from "./data";
import { Heart } from "lucide-react";

export default function Products() {
  const { category } = useParams();
  const { addToCart } = useCart();
  const { toggleWishlist, isInWishlist } = useWishlist();

  const visible =
    category === "all" || !category
      ? CATALOG
      : CATALOG.filter((p) => p.category === category || p.category === "both");

  return (
    <div style={{ padding: "40px 60px" }}>
      <h2 style={{ fontFamily: "var(--font-heading)", marginBottom: "40px", textTransform: "uppercase", fontSize: "28px" }}>
        {category ? category.replace("-", " ") : "All Products"}
      </h2>

      <div style={styles.grid}>
        {visible.map((p) => (
          <div key={p.id} style={styles.card}>
            {/* IMAGE CONTAINER WITH HEART OVERLAY */}
            <div style={styles.imgWrap}>
              <div
                style={{ ...styles.img, backgroundImage: `url(${p.img})` }}
              />
              <button
                onClick={() => toggleWishlist(p)}
                style={styles.wishlistBtn}
              >
                <Heart
                  size={20}
                  fill={isInWishlist(p.id) ? "black" : "none"}
                  color={isInWishlist(p.id) ? "black" : "#1f1f1f"}
                  strokeWidth={1.5}
                />
              </button>
            </div>

            <div style={styles.info}>
              <div style={styles.name}>{p.name}</div>
              <div style={styles.price}>â‚¹{p.price}</div>
              <button style={styles.btn} onClick={() => addToCart(p)}>
                ADD TO CART
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(280px, 1fr))",
    gap: "40px"
  },
  card: { position: "relative" },
  imgWrap: { position: "relative", overflow: "hidden" },
  img: {
    height: "360px",
    backgroundSize: "cover",
    backgroundPosition: "center",
    marginBottom: "15px"
  },
  wishlistBtn: {
    position: "absolute",
    top: "15px",
    right: "15px",
    background: "#fff",
    borderRadius: "50%",
    width: "36px",
    height: "36px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    cursor: "pointer",
    boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
    transition: "transform 0.2s"
  },
  info: { textAlign: "center" },
  name: { fontFamily: "var(--font-heading)", fontSize: "17px", marginBottom: "8px" },
  price: { color: "#555", fontSize: "15px", marginBottom: "15px" },
  btn: {
    padding: "12px 0",
    width: "100%",
    background: "#1f1f1f",
    color: "#fff",
    fontSize: "13px",
    letterSpacing: "1px",
    textTransform: "uppercase",
    transition: "0.3s"
  }
};
