import { Link } from "react-router-dom";
import { useCart } from "./context/CartContext";
import logo from "./assets/floris-logo.png";
import { Menu, Search, ShoppingBag, User } from "lucide-react"; // Install: npm install lucide-react

export default function Navbar({ onOpenMenu, onOpenCart }) {
  const { cart } = useCart();

  return (
    <>
      {/* TOP ANNOUNCEMENT BAR */}
      <div style={styles.topBar}>
        <span>Complimentary Gift on Orders Above â‚¹2500 | Free Shipping</span>
      </div>

      {/* MAIN HEADER */}
      <header style={styles.header}>
        {/* LEFT: HAMBURGER & SEARCH */}
        <div style={styles.left}>
          <button onClick={onOpenMenu} style={styles.iconBtn}>
            <Menu size={24} strokeWidth={1.5} />
          </button>
          <button style={styles.iconBtn}>
            <Search size={22} strokeWidth={1.5} />
          </button>
        </div>

        {/* CENTER: LOGO */}
        <div style={styles.center}>
          <Link to="/">
            <img src={logo} alt="Floris" style={styles.logo} />
          </Link>
        </div>

        {/* RIGHT: ACCOUNT & CART */}
        <div style={styles.right}>
          <button style={styles.iconBtn}>
            <User size={22} strokeWidth={1.5} />
          </button>
          <button onClick={onOpenCart} style={styles.iconBtn}>
            <div style={{position: 'relative'}}>
              <ShoppingBag size={22} strokeWidth={1.5} />
              {cart.length > 0 && <span style={styles.badge}>{cart.length}</span>}
            </div>
          </button>
        </div>
      </header>
    </>
  );
}

const styles = {
  topBar: { background: "#1f1f1f", color: "#fff", textAlign: "center", padding: "10px", fontSize: "12px", letterSpacing: "1px" },
  header: { display: "grid", gridTemplateColumns: "1fr auto 1fr", padding: "20px 40px", borderBottom: "1px solid #e2e2e2", position: "sticky", top: 0, background: "#fff", zIndex: 800 },
  left: { display: "flex", gap: "20px", alignItems: "center" },
  right: { display: "flex", gap: "20px", alignItems: "center", justifySelf: "end" },
  logo: { height: "50px" },
  iconBtn: { padding: 0, display: "flex", alignItems: "center" },
  badge: { position: "absolute", top: "-5px", right: "-8px", background: "#c5a98d", color: "#fff", fontSize: "10px", width: "16px", height: "16px", borderRadius: "50%", display: "flex", justifyContent: "center", alignItems: "center" }
};
