import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useCart } from "./context/CartContext";
import Checkout from "./Checkout";

function Home() {
  const [products, setProducts] = useState([]);
  const { addToCart, cart, totalAmount } = useCart();

  useEffect(() => {
    fetch("http://localhost:5001/api/products")
      .then(res => res.json())
      .then(data => setProducts(data));
  }, []);

  return (
    <div style={styles.page}>
      <h1 style={styles.heading}>Floris Herbals ðŸŒ¿</h1>
      <p>Organic oils & natural fragrances</p>

      <div style={styles.cartBar}>
        ðŸ›’ Items: {cart.length} | Total: â‚¹{totalAmount} |
        <Link to="/checkout"> Go to Checkout</Link>
      </div>

      <div style={styles.grid}>
        {products.map(p => (
          <div key={p._id} style={styles.card}>
            <h3>{p.name}</h3>
            <p>{p.size}</p>
            <p>â‚¹{p.price}</p>
            <button onClick={() => addToCart(p)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default function App() {
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/checkout" element={<Checkout />} />
      </Routes>
    </BrowserRouter>
  );
}

const styles = {
  page: { padding: 40, fontFamily: "Georgia", background: "#f4f8f3" },
  heading: { color: "#2f4f2f" },
  cartBar: { margin: "20px 0", padding: 10, background: "#e3efe0" },
  grid: { display: "grid", gridTemplateColumns: "repeat(3,1fr)", gap: 20 },
  card: { background: "#fff", padding: 20, borderRadius: 8 }
};
