import { useState } from "react";
import { Link } from "react-router-dom";
import { X } from "lucide-react";
import { PRODUCTS } from "../data"; // Imports your product list

export default function SearchDrawer({ isOpen, close }) {
  const [query, setQuery] = useState("");

  // Filter products based on search query
  const results = query
    ? PRODUCTS.filter(p => p.name.toLowerCase().includes(query.toLowerCase()))
    : [];

  return (
    <div className={`drawer drawer-left ${isOpen ? "open" : ""}`} style={{ width: '400px' }}>
      <div className="drawer-header">
        <span style={{ fontFamily: 'var(--font-heading)', fontSize: 20 }}>Search</span>
        <button onClick={close}><X size={24} /></button>
      </div>

      <div className="drawer-body">
        <input
          type="text"
          placeholder="Search for products..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={styles.input}
          autoFocus
        />

        <div style={{ marginTop: "20px" }}>
          {results.length > 0 ? (
            results.map((p) => (
              <Link
                to={`/products/all`} // You can make a specific product page later
                key={p.id}
                style={styles.resultItem}
                onClick={close}
              >
                <img src={p.img} alt={p.name} style={styles.img} />
                <div>
                  <div style={styles.name}>{p.name}</div>
                  <div style={styles.price}>â‚¹{p.price}</div>
                </div>
              </Link>
            ))
          ) : (
            query && <p style={{ color: "#999" }}>No products found.</p>
          )}
        </div>
      </div>
    </div>
  );
}

const styles = {
  input: { width: "100%", padding: "12px", fontSize: "16px", border: "1px solid #ccc", borderRadius: "0" },
  resultItem: { display: "flex", gap: "15px", marginBottom: "15px", alignItems: "center", textDecoration: "none", color: "inherit" },
  img: { width: "50px", height: "50px", objectFit: "cover" },
  name: { fontSize: "14px", fontWeight: "500" },
  price: { fontSize: "13px", color: "#666" }
};
