import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";
import { useNavigate } from "react-router-dom";

const Wishlist = () => {
  const { wishlist, removeFromWishlist, addToCart } = useContext(CartContext);
  const navigate = useNavigate();

  const handleMoveToCart = (product) => {
    addToCart(product);
    removeFromWishlist(product._id);
  };

  const handleAddAllToCart = () => {
    wishlist.forEach((product) => {
      addToCart(product);
      removeFromWishlist(product._id);
    });
    navigate("/cart"); // Assumes you have a cart route, if not it redirects to home usually
  };

  return (
    <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
      <h1>❤️ My Wishlist</h1>
      {wishlist.length === 0 ? (
        <p>Your wishlist is empty.</p>
      ) : (
        <>
          <button onClick={handleAddAllToCart} style={{ marginBottom: "20px", padding: "10px 20px", background: "#007bff", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}>
            Add All to Cart
          </button>
          <div style={{ display: "grid", gap: "20px" }}>
            {wishlist.map((item) => (
              <div key={item._id} style={{ display: "flex", justifyContent: "space-between", border: "1px solid #ddd", padding: "15px", borderRadius: "8px" }}>
                <div style={{ display: "flex", gap: "15px" }}>
                  <img src={item.image} alt={item.name} style={{ width: "60px", height: "60px", objectFit: "cover" }} />
                  <div><h3>{item.name}</h3><p>₹{item.price}</p></div>
                </div>
                <div>
                  <button onClick={() => handleMoveToCart(item)} style={{ background: "#28a745", color: "#fff", padding: "8px", border: "none", marginRight: "10px" }}>Move to Cart</button>
                  <button onClick={() => removeFromWishlist(item._id)} style={{ background: "#dc3545", color: "#fff", padding: "8px", border: "none" }}>Remove</button>
                </div>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};
export default Wishlist;
