import { Link } from "react-router-dom";
import { useCart } from "./context/CartContext";

export default function Navbar() {
  const { cart } = useCart();

  return (
    <>
      {/* TOP BAR */}
      <header style={styles.header}>
        <div style={styles.leftIcons}>
          <span>‚ò∞</span>
          <span>üîç</span>
        </div>

        <div style={styles.logo}>
          <Link to="/">FLORIS HERBALS</Link>
        </div>

        <div style={styles.cart}>
          <Link to="/checkout">Cart ({cart.length})</Link>
        </div>
      </header>

      {/* MENU */}
      <nav style={styles.menu}>
        <Link to="/products/skin">Skin Care</Link>
        <Link to="/products/hair">Hair Care</Link>
        <Link to="/products/all">All Products</Link>
      </nav>
    </>
  );
}

const styles = {
  header: {
    display: "grid",
    gridTemplateColumns: "1fr auto 1fr",
    alignItems: "center",
    padding: "18px 40px",
    background: "#faf9f6",
    borderBottom: "1px solid #e6e6e6",
    position: "sticky",
    top: 0,
    zIndex: 100
  },
  leftIcons: {
    display: "flex",
    gap: "18px",
    fontSize: "18px"
  },
  logo: {
    fontFamily: "Playfair Display, serif",
    fontSize: "22px",
    letterSpacing: "2px"
  },
  cart: {
    justifySelf: "end",
    fontSize: "14px"
  },
  menu: {
    display: "flex",
    justifyContent: "center",
    gap: "64px",
    padding: "14px 0",
    borderBottom: "1px solid #e6e6e6",
    fontFamily: "Playfair Display, serif",
    fontSize: "15px"
  }
};
