import { BrowserRouter, Routes, Route } from "react-router-dom";
import { useState } from "react";
import Navbar from "./Navbar";
import Home from "./Home";
import Products from "./Products";
import Checkout from "./Checkout";
import Sidebar from "./components/Sidebar";
import CartDrawer from "./components/CartDrawer";
import SearchDrawer from "./components/SearchDrawer"; // IMPORT NEW COMPONENT

export default function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isSearchOpen, setIsSearchOpen] = useState(false); // NEW STATE

  const closeAll = () => {
    setIsMenuOpen(false);
    setIsCartOpen(false);
    setIsSearchOpen(false);
  };

  return (
    <BrowserRouter>
      {/* DRAWERS */}
      <Sidebar isOpen={isMenuOpen} close={closeAll} />
      <CartDrawer isOpen={isCartOpen} close={closeAll} />
      <SearchDrawer isOpen={isSearchOpen} close={closeAll} /> {/* NEW COMPONENT */}

      {/* BACKGROUND OVERLAY */}
      <div
        className={`overlay ${isMenuOpen || isCartOpen || isSearchOpen ? "open" : ""}`}
        onClick={closeAll}
      />

      <Navbar
        onOpenMenu={() => setIsMenuOpen(true)}
        onOpenCart={() => setIsCartOpen(true)}
        onOpenSearch={() => setIsSearchOpen(true)} // PASS TRIGGER
      />

      <Routes>
        <Route path="/" element={<Home />} />
        {/* Note: In Products.js, you'll need to update it to use the new data.js if you want full sync */}
        <Route path="/products/:category" element={<Products />} />
        <Route path="/checkout" element={<Checkout />} />
      </Routes>
    </BrowserRouter>
  );
}
