import { Link } from "react-router-dom";
import { useCart } from "./context/CartContext";
import logo from "./assets/floris-logo.png";
import { Menu, Search, ShoppingBag, User } from "lucide-react";
import { MENU_ITEMS } from "./data";

export default function Navbar({ onOpenMenu, onOpenCart, onOpenSearch }) {
  const { cart } = useCart();

  return (
    <>
      <div style={styles.topBar}>
        <span>Complimentary Gift on Orders Above â‚¹2500 | Free Shipping</span>
      </div>

      <header style={styles.header}>
        {/* LEFT */}
        <div style={styles.left}>
          <button onClick={onOpenMenu} style={styles.iconBtn}>
            <Menu size={24} strokeWidth={1.5} />
          </button>
          <button onClick={onOpenSearch} style={styles.iconBtn}>
            <Search size={22} strokeWidth={1.5} />
          </button>
        </div>

        {/* CENTER */}
        <div style={styles.center}>
          <Link to="/">
            <img src={logo} alt="Floris" style={styles.logo} />
          </Link>
        </div>

        {/* RIGHT */}
        <div style={styles.right}>
          <button style={styles.iconBtn}><User size={22} strokeWidth={1.5} /></button>
          <button onClick={onOpenCart} style={styles.iconBtn}>
            <div style={{ position: 'relative' }}>
              <ShoppingBag size={22} strokeWidth={1.5} />
              {cart.length > 0 && <span style={styles.badge}>{cart.length}</span>}
            </div>
          </button>
        </div>
      </header>

      {/* DESKTOP NAV MENU (The "Top Menu" you requested) */}
      <nav style={styles.navBar}>
        {MENU_ITEMS.map((item) => (
          <Link key={item.label} to={item.path} style={styles.navLink}>
            {item.label}
          </Link>
        ))}
      </nav>
    </>
  );
}

const styles = {
  topBar: { background: "#1f1f1f", color: "#fff", textAlign: "center", padding: "10px", fontSize: "12px", letterSpacing: "1px" },
  header: { display: "grid", gridTemplateColumns: "1fr auto 1fr", padding: "15px 40px", borderBottom: "1px solid #eee", background: "#fff", alignItems: "center" },
  left: { display: "flex", gap: "20px" },
  right: { display: "flex", gap: "20px", justifySelf: "end" },
  logo: { height: "45px" },
  iconBtn: { padding: 0, cursor: "pointer" },
  badge: { position: "absolute", top: "-5px", right: "-8px", background: "#c5a98d", color: "#fff", fontSize: "10px", width: "16px", height: "16px", borderRadius: "50%", display: "flex", justifyContent: "center", alignItems: "center" },
  navBar: { display: "flex", justifyContent: "center", gap: "30px", padding: "15px 0", borderBottom: "1px solid #eee", fontSize: "14px", textTransform: "uppercase", letterSpacing: "0.5px" },
  navLink: { color: "#000", fontWeight: "500", textDecoration: "none" }
};
