import { useState } from "react";

const slides = [
  {
    title: "PURE HERBAL OILS",
    subtitle: "Crafted for skin, hair & wellness",
    image: "https://images.unsplash.com/photo-1615485737651-580c9159c89d"
  },
  {
    title: "AROMATHERAPY ESSENTIALS",
    subtitle: "Relax, restore and rebalance",
    image: "https://images.unsplash.com/photo-1608571423539-e951b7b3c3f4"
  },
  {
    title: "NATURE'S HEALING TOUCH",
    subtitle: "From leaf to bottle",
    image: "https://images.unsplash.com/photo-1597076545399-91a3ff0e3c3b"
  }
];

export default function Home() {
  const [index, setIndex] = useState(0);

  return (
    <>
      <section
        style={{
          ...styles.slider,
          backgroundImage: `url(${slides[index].image})`
        }}
      >
        <div style={styles.overlay}>
          <h1>{slides[index].title}</h1>
          <p>{slides[index].subtitle}</p>
        </div>

        <div className="slider-arrow left" onClick={() => setIndex((index - 1 + slides.length) % slides.length)}>‹</div>
        <div className="slider-arrow right" onClick={() => setIndex((index + 1) % slides.length)}>›</div>
      </section>

      <section style={styles.collections}>
        {[
          "Winter Combo Collection",
          "Complete Hair Care Pack",
          "Complete Skin Care Pack"
        ].map((c, i) => (
          <div key={i} style={styles.collectionCard}>
            <div style={styles.collectionImg}></div>
            <span>{c}</span>
          </div>
        ))}
      </section>
    </>
  );
}

const styles = {
  slider: {
    height: "520px",
    backgroundSize: "cover",
    backgroundPosition: "center",
    position: "relative"
  },
  overlay: {
    position: "absolute",
    top: "50%",
    left: "80px",
    transform: "translateY(-50%)",
    color: "#fff",
    maxWidth: "420px"
  },
  collections: {
    display: "grid",
    gridTemplateColumns: "repeat(3, 1fr)",
    gap: "28px",
    padding: "64px"
  },
  collectionCard: {
    textAlign: "center",
    cursor: "pointer",
    fontFamily: "Playfair Display, serif"
  },
  collectionImg: {
    height: "220px",
    background: "#e5e5e5",
    marginBottom: "14px"
  }
};
