import { BrowserRouter, Routes, Route } from "react-router-dom";
import { useState } from "react";
import Home from "./Home";
import Checkout from "./Checkout";
import { useCart } from "./context/CartContext";

function Navbar({ active, setActive }) {
  const { cart } = useCart();

  return (
    <>
      {/* Top header */}
      <header style={styles.header}>
        <div style={styles.leftIcons}>
          <span style={styles.icon}>‚ò∞</span>
          <span style={styles.icon}>üîç</span>
        </div>

        <div style={styles.logo}>FLORIS HERBALS</div>

        <div style={{ fontSize: 14 }}>Cart ({cart.length})</div>
      </header>

      {/* Category menu */}
      <nav style={styles.nav}>
        {["skin", "hair", "all"].map((cat) => (
          <span
            key={cat}
            onClick={() => setActive(cat)}
            style={{
              ...styles.navItem,
              fontWeight: active === cat ? "500" : "400",
              borderBottom: active === cat ? "1px solid #1f1f1f" : "none"
            }}
          >
            {cat === "skin"
              ? "Skin Care"
              : cat === "hair"
              ? "Hair Care"
              : "All Products"}
          </span>
        ))}
      </nav>
    </>
  );
}

export default function App() {
  const [activeCategory, setActiveCategory] = useState("all");

  return (
    <BrowserRouter>
      <Navbar active={activeCategory} setActive={setActiveCategory} />
      <Routes>
        <Route
          path="/"
          element={<Home activeCategory={activeCategory} />}
        />
        <Route path="/checkout" element={<Checkout />} />
      </Routes>
    </BrowserRouter>
  );
}

const styles = {
  header: {
    position: "sticky",
    top: 0,
    zIndex: 100,
    display: "grid",
    gridTemplateColumns: "1fr auto 1fr",
    alignItems: "center",
    padding: "18px 32px",
    background: "#faf9f7",
    borderBottom: "1px solid #e6e6e6"
  },
  leftIcons: {
    display: "flex",
    gap: "16px"
  },
  icon: {
    fontSize: "18px",
    cursor: "pointer"
  },
  logo: {
    justifySelf: "center",
    fontFamily: "Playfair Display, serif",
    letterSpacing: "2px",
    fontSize: "18px"
  },
  nav: {
    display: "flex",
    justifyContent: "center",
    gap: "56px", // üëà increased spacing
    padding: "16px 0",
    borderBottom: "1px solid #e6e6e6",
    fontFamily: "Playfair Display, serif",
    fontSize: "15px"
  },
  navItem: {
    cursor: "pointer",
    paddingBottom: "4px"
  }
};
