import { Link } from "react-router-dom";
import { X } from "lucide-react";
import { MENU_ITEMS } from "../data"; // Importing the shared menu list

export default function Sidebar({ isOpen, close }) {
  return (
    <div className={`drawer drawer-left ${isOpen ? "open" : ""}`}>
      <div className="drawer-header">
        <span style={{ fontFamily: 'var(--font-heading)', fontSize: 20 }}>Menu</span>
        <button onClick={close}><X size={24} /></button>
      </div>

      <div className="drawer-body">
        <div style={styles.list}>
          {MENU_ITEMS.map((item) => (
            <Link
              key={item.label}
              to={item.path}
              style={styles.item}
              onClick={close}
            >
              {item.label}
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}

const styles = {
  list: { display: "flex", flexDirection: "column" },
  item: { padding: "15px 0", borderBottom: "1px solid #f0f0f0", fontSize: "15px", color: "#1f1f1f", textTransform: "uppercase", letterSpacing: "1px" }
};
