import { BrowserRouter, Routes, Route } from "react-router-dom";
import { useState } from "react";
import Navbar from "./Navbar";
import Home from "./Home";
import Products from "./Products";
import Checkout from "./Checkout";
import Sidebar from "./components/Sidebar"; // We will create this
import CartDrawer from "./components/CartDrawer"; // We will create this

export default function App() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isCartOpen, setIsCartOpen] = useState(false);

  return (
    <BrowserRouter>
      {/* GLOBAL DRAWERS */}
      <Sidebar isOpen={isMenuOpen} close={() => setIsMenuOpen(false)} />
      <CartDrawer isOpen={isCartOpen} close={() => setIsCartOpen(false)} />

      {/* OVERLAY for Drawers */}
      <div
        className={`overlay ${isMenuOpen || isCartOpen ? "open" : ""}`}
        onClick={() => { setIsMenuOpen(false); setIsCartOpen(false); }}
      />

      <Navbar
        onOpenMenu={() => setIsMenuOpen(true)}
        onOpenCart={() => setIsCartOpen(true)}
      />

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/products/:category" element={<Products />} />
        <Route path="/checkout" element={<Checkout />} />
      </Routes>
    </BrowserRouter>
  );
}
