import { useState } from "react";

const slides = [
  {
    title: "NOURISHING RITUALS",
    subtitle: "Pure oils for skin & hair wellness",
    bg: "#6c7a6a"
  },
  {
    title: "WINTER CARE ESSENTIALS",
    subtitle: "Deep hydration & protection",
    bg: "#7b6f63"
  },
  {
    title: "HERBAL DAILY CARE",
    subtitle: "Traditionally crafted oils",
    bg: "#5e6f7a"
  }
];

export default function Home() {
  const [index, setIndex] = useState(0);

  const prev = () =>
    setIndex((index - 1 + slides.length) % slides.length);
  const next = () =>
    setIndex((index + 1) % slides.length);

  return (
    <>
      {/* HERO SLIDER */}
      <div style={{ ...styles.slider, background: slides[index].bg }}>
        <div style={styles.sliderText}>
          <h1>{slides[index].title}</h1>
          <p>{slides[index].subtitle}</p>
          <button style={styles.shopBtn}>SHOP NOW</button>
        </div>

        <div className="slider-arrow left" onClick={prev}>‹</div>
        <div className="slider-arrow right" onClick={next}>›</div>
      </div>

      {/* COLLECTION BLOCKS */}
      <div style={styles.collections}>
        {[
          "Winter Combo Collection",
          "Complete Hair Care Pack",
          "Complete Skin Care Pack"
        ].map((name, i) => (
          <div key={i} style={styles.collectionCard}>
            <div style={styles.collectionImg}></div>
            <span>{name}</span>
          </div>
        ))}
      </div>
    </>
  );
}

const styles = {
  slider: {
    height: "520px",
    color: "white",
    position: "relative",
    display: "flex",
    alignItems: "center",
    paddingLeft: "80px"
  },
  sliderText: {
    maxWidth: "420px"
  },
  shopBtn: {
    marginTop: "20px",
    padding: "10px 24px",
    background: "white",
    border: "none",
    fontSize: "14px",
    cursor: "pointer"
  },
  collections: {
    display: "grid",
    gridTemplateColumns: "repeat(3, 1fr)",
    gap: "28px",
    padding: "60px"
  },
  collectionCard: {
    textAlign: "center",
    cursor: "pointer"
  },
  collectionImg: {
    height: "220px",
    background: "#e5e5e5",
    marginBottom: "14px"
  }
};
