import { useCart } from "./context/CartContext";

const PRODUCTS = [
  // Hair care
  { name: "Onion Oil", price: 599, category: "hair" },
  { name: "Almond Oil", price: 549, category: "hair" },
  { name: "Coconut Oil", price: 399, category: "hair" },
  { name: "Rosemary Oil", price: 649, category: "hair" },

  // Skin care
  { name: "Jojoba Oil", price: 699, category: "skin" },
  { name: "Coconut Oil", price: 399, category: "skin" },
  { name: "Onion Mustard Oil", price: 599, category: "skin" }
];

export default function Home({ activeCategory }) {
  const { addToCart } = useCart();

  const visible =
    activeCategory === "all"
      ? PRODUCTS
      : PRODUCTS.filter(p => p.category === activeCategory);

  return (
    <div style={styles.wrapper}>
      <div style={styles.grid}>
        {visible.map((p, i) => (
          <div key={i} style={styles.card}>
            <div style={styles.imageBox}>
              <div style={styles.image}>ðŸŒ¿</div>
            </div>

            <div style={styles.name}>{p.name}</div>
            <div style={styles.price}>â‚¹{p.price}</div>

            <button
              style={styles.button}
              onClick={() => addToCart(p)}
            >
              ADD TO CART
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  wrapper: {
    padding: "64px"
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(260px, 1fr))",
    gap: "56px"
  },
  card: {
    cursor: "pointer"
  },
  imageBox: {
    height: "340px",
    background: "#f1f1f1",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    overflow: "hidden"
  },
  image: {
    fontSize: "42px",
    transition: "transform 0.4s ease"
  },
  name: {
    fontFamily: "Playfair Display, serif",
    marginTop: "16px",
    fontSize: "16px"
  },
  price: {
    marginTop: "6px",
    fontSize: "14px",
    color: "#6f6f6f"
  },
  button: {
    marginTop: "16px",
    padding: "10px 0",
    width: "100%",
    border: "1px solid #1f1f1f",
    fontSize: "12px",
    letterSpacing: "1px",
    transition: "all 0.3s ease"
  }
};
