import { useEffect, useState } from "react";

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5001/api/products")
      .then(res => res.json())
      .then(data => setProducts(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div style={styles.page}>
      <h1 style={styles.heading}>Floris Herbals ðŸŒ¿</h1>
      <p style={styles.tagline}>Organic oils & natural fragrances</p>

      <div style={styles.grid}>
        {products.map(product => (
          <div key={product._id} style={styles.card}>
            <h3>{product.name}</h3>
            <p>{product.size}</p>
            <p style={styles.price}>â‚¹{product.price}</p>
            <p style={styles.desc}>{product.description}</p>
            <button style={styles.button}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  page: {
    padding: "40px",
    fontFamily: "Georgia, serif",
    background: "#f4f8f3",
    minHeight: "100vh"
  },
  heading: {
    color: "#2f4f2f"
  },
  tagline: {
    marginBottom: "30px"
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
    gap: "20px"
  },
  card: {
    background: "white",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)"
  },
  price: {
    fontWeight: "bold",
    margin: "10px 0"
  },
  desc: {
    fontSize: "14px",
    color: "#555"
  },
  button: {
    marginTop: "10px",
    padding: "10px",
    width: "100%",
    background: "#5a7f4f",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer"
  }
};

export default App;
