import { useCart } from "../context/CartContext";
import { Link } from "react-router-dom";
import { X, Trash2 } from "lucide-react";

export default function CartDrawer({ isOpen, close }) {
  const { cart, addToCart, removeFromCart, totalAmount } = useCart();
  // Note: Add removeFromCart to your Context if not present, or just hide it for now

  return (
    <div className={`drawer drawer-right ${isOpen ? "open" : ""}`}>
      <div className="drawer-header">
        <span style={{fontFamily: 'var(--font-heading)', fontSize: 20}}>Shopping Bag ({cart.length})</span>
        <button onClick={close}><X size={24} /></button>
      </div>

      <div className="drawer-body">
        {cart.length === 0 ? (
          <p style={{textAlign:'center', marginTop: 40, color: '#999'}}>Your bag is empty.</p>
        ) : (
          cart.map((item, i) => (
            <div key={i} style={styles.item}>
              <img src={item.img || "https://via.placeholder.com/80"} alt={item.name} style={styles.img} />
              <div>
                <div style={styles.name}>{item.name}</div>
                <div style={styles.price}>₹{item.price}</div>
              </div>
            </div>
          ))
        )}
      </div>

      <div className="drawer-footer">
        <div style={styles.totalRow}>
          <span>Subtotal</span>
          <span>₹{totalAmount}</span>
        </div>
        <Link to="/checkout" onClick={close}>
          <button className="btn-primary">PROCEED TO CHECKOUT</button>
        </Link>
      </div>
    </div>
  );
}

const styles = {
  item: { display: "flex", gap: "15px", marginBottom: "20px" },
  img: { width: "70px", height: "70px", objectFit: "cover" },
  name: { fontSize: "14px", fontWeight: "500", marginBottom: "5px" },
  price: { fontSize: "13px", color: "#666" },
  totalRow: { display: "flex", justifyContent: "space-between", marginBottom: "15px", fontWeight: "600" }
};
