import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const SLIDES = [
  {
    title: "The Art of Ayurveda",
    subtitle: "Discover pure, organic wellness",
    img: "https://images.unsplash.com/photo-1615485737651-580c9159c89d"
  },
  {
    title: "Luxury Hair Care",
    subtitle: "Root to tip nourishment",
    img: "https://images.unsplash.com/photo-1597076545399-91a3ff0e3c3b"
  },
  {
    title: "Glow Naturally",
    subtitle: "Skin essentials for the winter",
    img: "https://images.unsplash.com/photo-1556228720-195a672e8a03"
  }
];

export default function Home() {
  const [index, setIndex] = useState(0);

  // Auto-slide every 5 seconds
  useEffect(() => {
    const timer = setInterval(() => {
      setIndex((prev) => (prev + 1) % SLIDES.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div>
      {/* SLIDESHOW SECTION */}
      <section style={styles.hero}>
        {SLIDES.map((slide, i) => (
          <div
            key={i}
            style={{
              ...styles.slide,
              backgroundImage: `url(${slide.img})`,
              opacity: index === i ? 1 : 0
            }}
          >
            <div style={styles.overlay}>
              <h1 style={styles.title}>{slide.title}</h1>
              <p style={styles.sub}>{slide.subtitle}</p>
              <Link to="/products/all">
                <button className="btn-primary" style={{ maxWidth: 200 }}>Shop Now</button>
              </Link>
            </div>
          </div>
        ))}
      </section>

      {/* COLLECTIONS GRID */}
      <section style={styles.section}>
        <h2 style={{ textAlign: "center", marginBottom: 40, fontFamily: "var(--font-heading)" }}>
          Curated Collections
        </h2>
        <div style={styles.grid}>
          {["Best Sellers", "Skin Care", "Hair Care", "Gift Packs"].map((cat) => (
            <Link to="/products/all" key={cat} style={styles.card}>
              <div style={styles.cardImg}></div>
              <span style={styles.cardText}>{cat}</span>
            </Link>
          ))}
        </div>
      </section>
    </div>
  );
}

const styles = {
  hero: { height: "550px", position: "relative", overflow: "hidden", background: "#000" },
  slide: { position: "absolute", top: 0, left: 0, width: "100%", height: "100%", backgroundSize: "cover", backgroundPosition: "center", transition: "opacity 1s ease-in-out" },
  overlay: { position: "absolute", top: "50%", left: "10%", transform: "translateY(-50%)", color: "#fff", maxWidth: "500px" },
  title: { fontSize: "48px", marginBottom: "10px" },
  sub: { fontSize: "18px", marginBottom: "25px", fontWeight: "300" },
  section: { padding: "60px 20px", maxWidth: "1200px", margin: "0 auto" },
  grid: { display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))", gap: "20px" },
  card: { textAlign: "center", cursor: "pointer", display: "block" },
  cardImg: { height: "300px", background: "#e5e5e5", marginBottom: "15px" },
  cardText: { fontFamily: "var(--font-heading)", fontSize: "20px" }
};
