import { Link } from "react-router-dom";
import { useCart } from "./context/CartContext";

export default function Navbar() {
  const { cart } = useCart();

  return (
    <>
      <header style={styles.header}>
        <div style={styles.leftIcons}>
          {/* Hamburger */}
          <svg width="22" height="22" viewBox="0 0 24 24">
            <rect y="4" width="24" height="2" fill="#000" />
            <rect y="11" width="24" height="2" fill="#000" />
            <rect y="18" width="24" height="2" fill="#000" />
          </svg>

          {/* Search */}
          <svg width="22" height="22" viewBox="0 0 24 24">
            <circle cx="11" cy="11" r="7" stroke="#000" strokeWidth="2" fill="none"/>
            <line x1="16.5" y1="16.5" x2="22" y2="22" stroke="#000" strokeWidth="2"/>
          </svg>
        </div>

        <div style={styles.logo}>
          <Link to="/">FLORIS HERBALS</Link>
        </div>

        <div style={styles.cart}>
          <Link to="/checkout" style={styles.cartLink}>
            <svg width="24" height="24" viewBox="0 0 24 24">
              <circle cx="9" cy="21" r="1.5" fill="#000"/>
              <circle cx="18" cy="21" r="1.5" fill="#000"/>
              <path d="M2 3h2l3 13h11l3-8H6" fill="none" stroke="#000" strokeWidth="2"/>
            </svg>
            <span>({cart.length})</span>
          </Link>
        </div>
      </header>

      <nav style={styles.menu}>
        <Link to="/">Home</Link>
        <Link to="/products/skin">Skin Care</Link>
        <Link to="/products/hair">Hair Care</Link>
        <Link to="/products/all">All Products</Link>
      </nav>
    </>
  );
}

const styles = {
  header: {
    display: "grid",
    gridTemplateColumns: "1fr auto 1fr",
    alignItems: "center",
    padding: "18px 40px",
    background: "#faf9f6",
    borderBottom: "1px solid #e6e6e6",
    position: "sticky",
    top: 0,
    zIndex: 100
  },
  leftIcons: {
    display: "flex",
    gap: "18px",
    alignItems: "center"
  },
  logo: {
    fontFamily: "Playfair Display, serif",
    fontSize: "22px",
    letterSpacing: "2px"
  },
  cart: {
    justifySelf: "end"
  },
  cartLink: {
    display: "flex",
    gap: "6px",
    alignItems: "center"
  },
  menu: {
    display: "flex",
    justifyContent: "center",
    gap: "64px",
    padding: "14px 0",
    borderBottom: "1px solid #e6e6e6",
    fontFamily: "Playfair Display, serif",
    fontSize: "15px"
  }
};
