import React, { useEffect, useState, useContext } from "react";
import { CartContext } from "../context/CartContext";

const Home = () => {
  const [products, setProducts] = useState([]);
  const { addToCart, addToWishlist } = useContext(CartContext);

  useEffect(() => {
    fetch("https://floris-backend-ct26.onrender.com/api/products")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Error:", err));
  }, []);

  const perfumes = products.filter(p => p.category === "Perfume" || p.category === "Attar");
  const soaps = products.filter(p => p.category === "Soap" || p.category === "Soap Fragrance");
  const poojaOils = products.filter(p => p.category === "Pooja Oil");
  const rawMaterials = products.filter(p => p.category === "Raw Material");

  const Section = ({ title, items }) => (
    <div style={{ marginBottom: "40px" }}>
      <h2 style={{ borderBottom: "2px solid #ddd", paddingBottom: "10px" }}>{title}</h2>
      {items.length === 0 ? <p>Coming Soon...</p> : (
        <div style={{ display: "flex", gap: "20px", overflowX: "auto", padding: "10px 0" }}>
          {items.map((p) => (
            <div key={p._id} style={{ border: "1px solid #eee", padding: "10px", minWidth: "200px", borderRadius: "8px" }}>
              <img src={p.image} alt={p.name} style={{ width: "100%", height: "150px", objectFit: "cover" }} />
              <h3>{p.name}</h3>
              <p>₹{p.price}</p>
              <button onClick={() => addToCart(p)} style={{ width: "100%", background: "#28a745", color: "#fff", border: "none", padding: "8px", marginTop: "5px" }}>Add to Cart</button>
              <button onClick={() => addToWishlist(p)} style={{ width: "100%", background: "#ffc107", border: "none", padding: "8px", marginTop: "5px" }}>❤️ Wishlist</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  return (
    <div style={{ padding: "20px", maxWidth: "1200px", margin: "0 auto" }}>
      <h1>Welcome to Floris Herbals</h1>
      <Section title="Signature Fragrances" items={perfumes} />
      <Section title="Luxury Handmade Soaps" items={soaps} />
      <Section title="Sacred Pooja Oils" items={poojaOils} />
      <Section title="Premium Raw Materials" items={rawMaterials} />
    </div>
  );
};
export default Home;
