import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Home from "./Home";
import Checkout from "./Checkout";
import { useCart } from "./CartContext";

function Navbar() {
  const { cart } = useCart();

  return (
    <>
      {/* Top bar */}
      <header style={styles.header}>
        <button style={styles.icon}>‚ò∞</button>

        <div style={styles.logo}>FLORIS HERBALS</div>

        <div style={styles.icon}>üîç</div>
      </header>

      {/* Category bar */}
      <nav style={styles.nav}>
        <span>Skin Care</span>
        <span>Hair Care</span>
        <span>All Products</span>
        <Link to="/checkout">Cart ({cart.length})</Link>
      </nav>
    </>
  );
}

export default function App() {
  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/checkout" element={<Checkout />} />
      </Routes>
    </BrowserRouter>
  );
}

const styles = {
  header: {
    position: "sticky",
    top: 0,
    zIndex: 100,
    display: "grid",
    gridTemplateColumns: "1fr auto 1fr",
    alignItems: "center",
    padding: "18px 32px",
    background: "#faf9f7",
    borderBottom: "1px solid #e6e6e6"
  },
  logo: {
    justifySelf: "center",
    fontFamily: "Playfair Display, serif",
    letterSpacing: "2px",
    fontSize: "18px"
  },
  icon: {
    fontSize: "18px"
  },
  nav: {
    display: "flex",
    justifyContent: "center",
    gap: "40px",
    padding: "14px 0",
    borderBottom: "1px solid #e6e6e6",
    fontSize: "14px"
  }
};
