import { useState } from "react";
import { Link } from "react-router-dom";
import { X, ChevronRight, ArrowLeft } from "lucide-react";

const MENU_DATA = {
  skincare: ["Cleansers", "Toners", "Moisturizers", "Serums"],
  haircare: ["Hair Oils", "Shampoos", "Conditioners", "Color"],
  bodycare: ["Body Wash", "Lotions", "Soaps", "Scrubs"]
};

export default function Sidebar({ isOpen, close }) {
  const [activeCategory, setActiveCategory] = useState(null);

  return (
    <div className={`drawer drawer-left ${isOpen ? "open" : ""}`}>
      {/* HEADER */}
      <div className="drawer-header">
        {activeCategory ? (
          <button onClick={() => setActiveCategory(null)} style={{display:'flex', gap:10}}>
            <ArrowLeft size={20} /> Back
          </button>
        ) : (
          <span style={{fontFamily: 'var(--font-heading)', fontSize: 20}}>Menu</span>
        )}
        <button onClick={close}><X size={24} /></button>
      </div>

      {/* BODY */}
      <div className="drawer-body">
        {/* LEVEL 1: MAIN CATEGORIES */}
        {!activeCategory && (
          <div style={styles.list}>
            {Object.keys(MENU_DATA).map((cat) => (
              <div key={cat} style={styles.item} onClick={() => setActiveCategory(cat)}>
                <span style={{textTransform: 'capitalize'}}>{cat}</span>
                <ChevronRight size={16} color="#999" />
              </div>
            ))}
            <Link to="/products/all" style={styles.item} onClick={close}>
              View All Products
            </Link>
          </div>
        )}

        {/* LEVEL 2: SUB CATEGORIES */}
        {activeCategory && (
          <div style={styles.list}>
            <div style={styles.catTitle}>{activeCategory}</div>
            {MENU_DATA[activeCategory].map((sub) => (
              <Link
                key={sub}
                to={`/products/${activeCategory}`} // In real app, use sub-cat URL
                style={styles.subItem}
                onClick={close}
              >
                {sub}
              </Link>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  list: { display: "flex", flexDirection: "column" },
  item: { padding: "15px 0", borderBottom: "1px solid #f0f0f0", display: "flex", justifyContent: "space-between", alignItems: "center", cursor: "pointer", fontSize: "15px" },
  subItem: { padding: "12px 0", fontSize: "14px", color: "#555" },
  catTitle: { fontFamily: "var(--font-heading)", fontSize: "24px", marginBottom: "20px", textTransform: "capitalize" }
};
