import { useParams } from "react-router-dom";
import { useCart } from "./context/CartContext";

const CATALOG = [
  // HAIR
  { name: "Onion Oil", price: 599, category: "hair", img: "https://images.unsplash.com/photo-1615485737651-580c9159c89d" },
  { name: "Rosemary Oil", price: 649, category: "hair", img: "https://images.unsplash.com/photo-1597076545399-91a3ff0e3c3b" },
  { name: "Peppermint Oil", price: 699, category: "hair", img: "https://images.unsplash.com/photo-1585386959984-a4155228f6d1" },
  { name: "Tea Tree Oil", price: 699, category: "hair", img: "https://images.unsplash.com/photo-1608571423539-e951b7b3c3f4" },

  // SKIN
  { name: "Jojoba Oil", price: 749, category: "skin", img: "https://images.unsplash.com/photo-1556228720-195a672e8a03" },
  { name: "Lavender Oil", price: 699, category: "skin", img: "https://images.unsplash.com/photo-1587502536263-929b19d4f3a1" },
  { name: "Bergamot Oil", price: 799, category: "skin", img: "https://images.unsplash.com/photo-1611077543694-dfca9b7b4e35" },

  // BOTH
  { name: "Coconut Oil", price: 399, category: "both", img: "https://images.unsplash.com/photo-1601004890684-d8cbf643f5f2" },
  { name: "Almond Oil", price: 549, category: "both", img: "https://images.unsplash.com/photo-1606836591695-4d58a73eba1e" },
  { name: "Eucalyptus Oil", price: 699, category: "both", img: "https://images.unsplash.com/photo-1608571423539-e951b7b3c3f4" },
  { name: "Orange Oil", price: 599, category: "both", img: "https://images.unsplash.com/photo-1582284540020-8acbe03f4924" }
];

export default function Products() {
  const { category } = useParams();
  const { addToCart } = useCart();

  const visible =
    category === "all"
      ? CATALOG
      : CATALOG.filter(p => p.category === category || p.category === "both");

  return (
    <div style={{ padding: "60px" }}>
      <h2 style={{ fontFamily: "Playfair Display, serif", marginBottom: "40px" }}>
        {category.toUpperCase()} PRODUCTS
      </h2>

      <div style={styles.grid}>
        {visible.map((p, i) => (
          <div key={i}>
            <div style={{ ...styles.img, backgroundImage: `url(${p.img})` }}></div>
            <div style={styles.name}>{p.name}</div>
            <div style={styles.price}>â‚¹{p.price}</div>
            <button style={styles.btn} onClick={() => addToCart(p)}>
              ADD TO CART
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(260px, 1fr))",
    gap: "48px"
  },
  img: {
    height: "320px",
    backgroundSize: "cover",
    backgroundPosition: "center"
  },
  name: {
    fontFamily: "Playfair Display, serif",
    marginTop: "14px"
  },
  price: {
    color: "#6f6f6f",
    marginTop: "6px"
  },
  btn: {
    marginTop: "14px",
    padding: "10px 0",
    width: "100%",
    border: "1px solid #000"
  }
};
